// Importando o React
import React, {useState} from "react";
// Importando os components necessários da lib react-materialize
import { Row, Col, Card } from 'react-materialize';
import { ElementProfile, StudentProfile } from "./profile.js";
import { ElementDescription } from './element_description'
import { ArtDescription } from "./art_description.js";


export const Home = (props) => {

const [number , setNumber] = useState()
// console.log(number)
  return (
    props.payload && props.payload.map ((item)=>{


   <Row>
      <input
        type= "number"
        onChange ={(event) =>{
          setNumber(event.target.value)
          props.handleChange(number)
        }}
        //value={value}
        placeholder={"digite o número atômico do elemento"}
        style={{
          width: 280
        }} />
      <Col m={3} s={12}>
        <ElementProfile
          element={item.element}
          number={item.number}
          symbol={item.symbol}
        />
        <StudentProfile
          student={item.student}
          word={item.word}
        />
      </Col>
      <Col m={8} s={12}>
        <h5 className="subtitle">Sobre o {item.element}</h5>
        <Card>
          <ElementDescription elementdesc={item.elementdesc} />
        </Card>
        <h5 className="subtitle">Sobre a Arte</h5>
        <Card>
          <ArtDescription artdesc={item.artdesc} />
        </Card>
      </Col>
    </Row>
      })
  )
};
